# %%
def task_to_templates():
    ttt = raw_task_to_templates
    templates = {}
    for key in ttt.keys():
        values = ttt[key]
        if key not in templates.keys(): templates[key] = []
        if key == "rte":
            # print(len(values))
            for prompt in values:
                template = "Provided that {{premise}}, " + prompt
                template += " {{hypothesis}} ||| {% if label != -1 %}{{ answer_choices[label] }}{% endif %}"
                templates[key].append(template)
        elif key == "wic":
            for prompt in values:
                template = "Provided a word {{word}}, " + prompt
                template += "{{sentence1}}\n\n{{sentence2}}\n\n||| {% if label != -1%}\n{{answer_choices[label]}}{% endif %}"
                templates[key].append(template)
        elif key == "boolq":
            for prompt in values:
                template = "Given a passage: {{passage}}" + prompt
                template += "|||\n\n{% if label != -1 %}\n\n{{answer_choices[label]}}\n{% endif %}"
                templates[key].append(template)
        elif key == 'e2e_nlg_cleaned':
            for prompt in values:
                template = '''Provided a triplet {% for feature in meaning_representation.split("]") %} {% set key = feature.split("[")[0].replace(",","")%} {% set value = feature.replace(",","").replace(key+"[", '') %}{% if value != "" %} {{key}} : {{value}} {% endif %}{%- endfor %} ''' + prompt
                template += ''' ||| {{human_reference}} '''
                templates[key].append(template)
        elif key == 'web_nlg_en':
            for prompt in values:
                template = '''Provided a triplet {{input| join(", ")}} ''' + prompt
                template += ''' ||| {{target}} '''
                templates[key].append(template)
        # else:
        #     raise ValueError(f"the template for task{key} is not implemented.") 
    return templates

# %%
import json

templates = task_to_templates()
# save 
with open("task_templates.json", "w") as f:
    json.dump(templates, f, indent=4)

